using namespace std;
class Solution {
 public:
  int longestPalindrome(string s) {
    unordered_map<char, int> char2cnt;
    for (char c : s) {
      ++char2cnt[c];
    }
    int res = 0;
    int have_odd = 0;
    for (auto& p : char2cnt) {
      int cnt = p.second;
      if (cnt % 2 == 0) {
        res += cnt;
      } else {
        res += cnt - 1;
        have_odd = 1;
      }
    }
    return res + have_odd;
  }
};

